import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import styles from '../styles/Login.module.css';

const MySwal = withReactContent(Swal);

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const navigate = useNavigate();

  const validateEmail = (email) => {
    const regex = /^[a-zA-Z0-9._-]+@(insucodos|comeduc)\.cl$/;
    return regex.test(email);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!validateEmail(email)) {
        throw new Error('Debes usar un correo institucional (@insucodos.cl o @comeduc.cl)');
      }

      if (email.endsWith('@comeduc.cl') && !password) {
        throw new Error('Por favor ingresa tu contrase√±a');
      }

      const response = await axios.post('http://localhost/sigaca/backend/api/login.php', {
        email,
        password: email.endsWith('@insucodos.cl') ? null : password
      });

      localStorage.setItem('sigaca_token', response.data.token);
      localStorage.setItem('sigaca_user', JSON.stringify(response.data.user));

      switch(response.data.user.rol) {
        case 'estudiante':
          navigate('/estudiante');
          break;
        case 'profesor':
          navigate('/profesor');
          break;
        case 'inspector':
          navigate('/inspector');
          break;
        default:
          navigate('/');
      }

    } catch (error) {
      let errorMessage = error.response?.data?.message || error.message;

      if (error.response?.status === 401) {
        errorMessage = 'Credenciales incorrectas';
      } else if (error.response?.status === 403) {
        errorMessage = 'Tu cuenta est√° desactivada';
      }

      MySwal.fire({
        title: 'Error',
        text: errorMessage,
        icon: 'error',
        confirmButtonText: 'Entendido',
        confirmButtonColor: '#1a237e'
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={styles.loginContainer}>
      <div className={styles.loginCard}>
        <div className={styles.loginHeader}>
          <img src="/assets/logo-liceo.png" alt="Logo Liceo" className={styles.logo} />
          <h2>Sistema SIGACA</h2>
          <p>Gesti√≥n de Atrasos Escolares</p>
        </div>

        <form onSubmit={handleSubmit} className={styles.loginForm}>
          <div className={styles.formGroup}>
            <label htmlFor="email">Correo Institucional</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="usuario@insucodos.cl o usuario@comeduc.cl"
              required
              className={!validateEmail(email) && email ? styles.invalid : ''}
            />
            {!validateEmail(email) && email && (
              <span className={styles.errorText}>Correo no v√°lido</span>
            )}
          </div>

          {email.endsWith('@comeduc.cl') && (
            <div className={`${styles.formGroup} ${styles.passwordGroup}`}>
              <label htmlFor="password">Contrase√±a</label>
              <div className={styles.passwordInputContainer}>
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Ingresa tu contrase√±a"
                  required
                />
                <button
                  type="button"
                  className={styles.togglePassword}
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? 'Ocultar contrase√±a' : 'Mostrar contrase√±a'}
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>
          )}

          <button type="submit" className={styles.loginButton} disabled={loading}>
            {loading ? (
              <>
                <span className={styles.spinner}></span> Cargando...
              </>
            ) : (
              'Ingresar'
            )}
          </button>
        </form>

        <div className={styles.loginFooter}>
          <p>¬øProblemas para ingresar? Contacta al administrador</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
