import React, { useState, useCallback, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';
import styles from '../styles/Login.module.css';

// Configuración de axios para la API
const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost/sigaca/backend/api',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
  },
});

const MySwal = withReactContent(Swal);

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [errors, setErrors] = useState({});
  const [isMounted, setIsMounted] = useState(true);
  const navigate = useNavigate();

  // Limpieza al desmontar
  useEffect(() => {
    return () => {
      setIsMounted(false);
    };
  }, []);

  // Validación de email
  const validateEmail = useCallback((email) => {
    const regex = /^[a-zA-Z0-9._-]+@(insucodos|comeduc)\.cl$/;
    return regex.test(email);
  }, []);

  // Validación de formulario
  const validateForm = useCallback(() => {
    const newErrors = {};
    
    if (!formData.email) {
      newErrors.email = 'El correo es requerido';
    } else if (!validateEmail(formData.email)) {
      newErrors.email = 'Debes usar un correo institucional (@insucodos.cl o @comeduc.cl)';
    }

    if (formData.email.endsWith('@comeduc.cl') && !formData.password) {
      newErrors.password = 'Por favor ingresa tu contraseña';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [formData, validateEmail]);

  // Manejo de cambios en el formulario
  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [id]: value,
    }));
    
    // Validación en tiempo real
    if (id === 'email' && value && !validateEmail(value)) {
      setErrors(prev => ({
        ...prev,
        email: 'Correo no válido',
      }));
    } else if (id === 'email') {
      setErrors(prev => ({
        ...prev,
        email: '',
      }));
    }
  };

  // Manejo del envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) return;
    
    setLoading(true);
    
    try {
      const payload = {
        email: formData.email,
        password: formData.email.endsWith('@insucodos.cl') ? null : formData.password
      };

      const response = await api.post('/login.php', payload);

      if (!isMounted) return;

      // Almacenamiento seguro de token y usuario
      localStorage.setItem('sigaca_token', response.data.token);
      localStorage.setItem('sigaca_user', JSON.stringify(response.data.user));

      // Redirección basada en rol
      const redirectPaths = {
        estudiante: '/estudiante',
        profesor: '/profesor',
        inspector: '/inspector',
      };

      navigate(redirectPaths[response.data.user.rol] || '/');

    } catch (error) {
      if (!isMounted) return;

      let errorMessage = 'Error al iniciar sesión';
      
      if (error.response) {
        switch(error.response.status) {
          case 400:
            errorMessage = error.response.data.message || 'Datos de entrada inválidos';
            break;
          case 401:
            errorMessage = 'Credenciales incorrectas';
            break;
          case 403:
            errorMessage = 'Tu cuenta está desactivada';
            break;
          case 500:
            errorMessage = 'Error del servidor. Por favor intenta más tarde';
            break;
          default:
            errorMessage = error.response.data.message || errorMessage;
        }
      } else if (error.request) {
        errorMessage = 'No se pudo conectar al servidor';
      }

      await MySwal.fire({
        title: 'Error',
        text: errorMessage,
        icon: 'error',
        confirmButtonText: 'Entendido',
        confirmButtonColor: '#1a237e',
        allowOutsideClick: false,
      });

    } finally {
      if (isMounted) {
        setLoading(false);
      }
    }
  };

  return (
    <div className={styles.loginContainer}>
      <div className={styles.loginCard}>
        <div className={styles.loginHeader}>
          <img 
            src="/assets/logo-liceo.png" 
            alt="Logo Liceo" 
            className={styles.logo}
            loading="lazy"
          />
          <h2>Sistema SIGACA</h2>
          <p>Gestión de Atrasos Escolares</p>
        </div>

        <form onSubmit={handleSubmit} className={styles.loginForm} noValidate>
          <div className={styles.formGroup}>
            <label htmlFor="email">Correo Institucional</label>
            <input
              type="email"
              id="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="usuario@insucodos.cl o usuario@comeduc.cl"
              required
              className={errors.email ? styles.invalid : ''}
              autoComplete="username"
              disabled={loading}
            />
            {errors.email && (
              <span className={styles.errorText}>{errors.email}</span>
            )}
          </div>

          {formData.email.endsWith('@comeduc.cl') && (
            <div className={`${styles.formGroup} ${styles.passwordGroup}`}>
              <label htmlFor="password">Contraseña</label>
              <div className={styles.passwordInputContainer}>
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder="Ingresa tu contraseña"
                  required
                  autoComplete="current-password"
                  disabled={loading}
                />
                <button
                  type="button"
                  className={styles.togglePassword}
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? 'Ocultar contraseña' : 'Mostrar contraseña'}
                  disabled={loading}
                >
                  {showPassword ? '👁️' : '👁️‍🗨️'}
                </button>
              </div>
              {errors.password && (
                <span className={styles.errorText}>{errors.password}</span>
              )}
            </div>
          )}

          <button 
            type="submit" 
            className={styles.loginButton} 
            disabled={loading || Object.keys(errors).length > 0}
            aria-busy={loading}
          >
            {loading ? (
              <>
                <span className={styles.spinner} aria-hidden="true"></span> 
                <span>Cargando...</span>
              </>
            ) : (
              'Ingresar'
            )}
          </button>
        </form>

        <div className={styles.loginFooter}>
          <p>¿Problemas para ingresar? Contacta al administrador</p>
          <p className={styles.versionInfo}>
            
          </p>
        </div>
      </div>
    </div>
  );
};

export default React.memo(Login);
